    public OrderHistoryDetail(OrderFile order) {
        model = new NonEditableTableModel();
        this.order = order;
        this.OrderID = order.getOrderID();
        setDefaultCloseOperation(javax.swing.WindowConstants.DISPOSE_ON_CLOSE);
        initComponents();
        OrderTable();
        int stars = order.getReview().getStars();
        if(stars==0){
            String comment = "The user has not left any review";
        }
        else{
            String comment = stars + "Stars\n" + order.getReview().getComment();
        }
        jLabel2.setText(comment);
    }
    
    private NonEditableTableModel model;
    String comment = "The user has not left any review";
    
    public void setOrder(OrderFile order){
        this.order = order;
    }
    
    
    public void OrderTable(){
        model = new NonEditableTableModel();
        model.addColumn("Food ID");
        model.addColumn("Name");
        model.addColumn("Price");
        model.addColumn("Vendor Name");
        model.addColumn("Quantity");
        
        try (BufferedReader reader = new BufferedReader(new FileReader(OrderID))) {
        String line;
        //boolean isFirstLine = true;

        while ((line = reader.readLine()) != null) {
           

            String[] MenuData = line.split(",");
            if (MenuData.length == 5) {
                String FoodId = MenuData[0];
                String Name = MenuData[1];
                String Price = MenuData[2];
                String vendor_name = MenuData[3];
                String Quantity = MenuData[4];
               
                model.addRow(new Object[]{FoodId, Name, Price, vendor_name, Quantity});
            } else {
                System.out.println("Invalid line: " + line);
            }
        }
        
        jLabel1.setText("Status: "+OrderFile.getStatus(OrderID));
        Order.setModel(model);
        }   
        catch (IOException ex) {
            Logger.getLogger(customerMenu.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

