    public void setTable(String VendorID){
        NonEditableTableModel model = new NonEditableTableModel();
        model.addColumn("CustomerID");
        model.addColumn("Stars");
        model.addColumn("Comment");
        model.addColumn("Date");
        
        Object[] ObjectList = ObjWriteRead.readAllObjectsFromFile("Orders.dat");
        double totalStars = 0;
        int reviewCount = 0;
        
        for(Object obj:ObjectList){
            if(obj instanceof OrderFile){
                OrderFile order = (OrderFile) obj;
                if(order.getVendorName().equals(VendorID)&&order.getReview().getStars()!=0){
                    model.addRow(new Object[]{order.getCustomerID(),order.getReview().getStars(),order.getReview().getComment(),order.getDate()});
                    totalStars += order.getReview().getStars();
                    reviewCount++;
                }
            }
        }
        
        jTable1.setModel(model);
        
        if (reviewCount > 0) {
            double averageStars = totalStars / reviewCount;
            jLabel1.setText("Average Stars: " + String.format("%.2f", averageStars));
        } else {
            jLabel1.setText("No reviews yet");
        }
    }

